import React, { useState } from 'react';
import { Box, Container, CssBaseline } from '@mui/material';
import Navbar from './components/Navbar';
import Suggestion from './components/Suggestion';
import Charts from './components/Charts';
import Filters from './components/Filters';
import SettingsButton from './components/SettingsButton'; // Import SettingsButton
import PreferencesDialog from './components/PreferencesDialog'; // Import Preferences Dialog

const App = () => {
  const [openPreferences, setOpenPreferences] = useState(false);

  const handleOpenPreferences = () => {
    setOpenPreferences(true);
  };

  const handleClosePreferences = () => {
    setOpenPreferences(false);
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      {/* Navbar */}
      <Navbar />

      <Box component="main" sx={{ flexGrow: 1, padding: 3, marginTop: 8 }}>
        <Container maxWidth="lg">
          {/* Settings Button */}
          <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 2 }}>
            <SettingsButton onClick={handleOpenPreferences} /> {/* Settings Button to open preferences */}
          </Box>

          {/* Preferences Dialog */}
          <PreferencesDialog open={openPreferences} onClose={handleClosePreferences} />

          {/* Filters */}
          <Filters />

          {/* Suggestion Component */}
          <Suggestion />

          {/* Charts Component */}
          <Charts />
        </Container>
      </Box>
    </Box>
  );
};

export default App;
