// components/Suggestion.js
import React from 'react';
import { Paper, Typography, Grid } from '@mui/material';
import PaletteIcon from '@mui/icons-material/Palette';

const Suggestion = () => {
  // Dummy data (you can fetch this from a server or API)
  const suggestedColors = ['Red', 'Blue', 'Yellow'];

  return (
    <Paper sx={{ padding: 2, marginTop: 8 }}>
      <Typography variant="h6" gutterBottom fontFamily="Roboto">
        Today's Dress Color Suggestions
      </Typography>
      <Grid container spacing={2}>
        {suggestedColors.map((color, index) => (
          <Grid item xs={4} key={index}>
            <Paper
              sx={{
                padding: 2,
                backgroundColor: color.toLowerCase(),
                color: '#fff',
                textAlign: 'center',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <PaletteIcon sx={{ marginRight: 1 }} />
              <Typography>{color}</Typography>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Paper>
  );
};

export default Suggestion;
