// App.js
import React from 'react';
import { AppBar, Toolbar, Typography, CssBaseline, Box, Container, Grid, Paper, List, ListItem, ListItemText } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
  },
  drawer: {
    width: 240,
    flexShrink: 0,
  },
  drawerPaper: {
    width: 240,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  toolbar: theme.mixins.toolbar,
}));

const App = () => {
  const classes = useStyles();

  return (
    <Box className={classes.root}>
      <CssBaseline />

      {/* AppBar */}
      <AppBar position="fixed" className={classes.appBar}>
        <Toolbar>
          <Typography variant="h6" noWrap>
            Daily Dress Color Suggestions for Women
          </Typography>
        </Toolbar>
      </AppBar>

      {/* Sidebar */}
      <Box className={classes.drawer} sx={{ width: 240 }}>
        <List component="nav" aria-label="main mailbox folders">
          <ListItem button>
            <ListItemText primary="Home" />
          </ListItem>
          <ListItem button>
            <ListItemText primary="Suggestions" />
          </ListItem>
          <ListItem button>
            <ListItemText primary="Trends" />
          </ListItem>
          <ListItem button>
            <ListItemText primary="Settings" />
          </ListItem>
        </List>
      </Box>

      {/* Main Content Area */}
      <Box component="main" className={classes.content}>
        <Container maxWidth="lg" sx={{ mt: 8 }}>
          <Typography variant="h4" gutterBottom>
            Today's Dress Color Suggestions
          </Typography>
          <Grid container spacing={3}>
            {/* Suggestion Grid */}
            <Grid item xs={12} md={8} lg={9}>
              <Paper sx={{ padding: 2 }}>
                <Typography variant="h6">Suggested Colors for Today</Typography>
                {/* You can add images, icons, or a more detailed component here */}
              </Paper>
            </Grid>

            {/* Charts Section */}
            <Grid item xs={12} md={4} lg={3}>
              <Paper sx={{ padding: 2 }}>
                <Typography variant="h6">Color Trends</Typography>
                {/* Add chart components like bar or pie chart here */}
              </Paper>
            </Grid>
          </Grid>
        </Container>
      </Box>
    </Box>
  );
};

export default App;
