// components/Filters.js
import React, { useState } from 'react';
import { Box, FormControl, InputLabel, MenuItem, Select, Button } from '@mui/material';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import EventIcon from '@mui/icons-material/Event';
import StyleIcon from '@mui/icons-material/Style';

const Filters = () => {
  const [location, setLocation] = useState('');
  const [season, setSeason] = useState('');
  const [preference, setPreference] = useState('');

  const handleFilter = () => {
    console.log('Location:', location, 'Season:', season, 'Preference:', preference);
    // Fetch or update suggestions based on filters
  };

  return (
    <Box sx={{ marginTop: 4 }}>
      <FormControl fullWidth sx={{ marginBottom: 2 }}>
        <InputLabel>
          <LocationOnIcon sx={{ marginRight: 1 }} />
          Location
        </InputLabel>
        <Select value={location} onChange={(e) => setLocation(e.target.value)}>
          <MenuItem value="City1">City1</MenuItem>
          <MenuItem value="City2">City2</MenuItem>
          <MenuItem value="City3">City3</MenuItem>
        </Select>
      </FormControl>

      <FormControl fullWidth sx={{ marginBottom: 2 }}>
        <InputLabel>
          <EventIcon sx={{ marginRight: 1 }} />
          Season
        </InputLabel>
        <Select value={season} onChange={(e) => setSeason(e.target.value)}>
          <MenuItem value="Winter">Winter</MenuItem>
          <MenuItem value="Spring">Spring</MenuItem>
          <MenuItem value="Summer">Summer</MenuItem>
          <MenuItem value="Fall">Fall</MenuItem>
        </Select>
      </FormControl>

      <FormControl fullWidth sx={{ marginBottom: 2 }}>
        <InputLabel>
          <StyleIcon sx={{ marginRight: 1 }} />
          Personal Preference
        </InputLabel>
        <Select value={preference} onChange={(e) => setPreference(e.target.value)}>
          <MenuItem value="Formal">Formal</MenuItem>
          <MenuItem value="Casual">Casual</MenuItem>
          <MenuItem value="Sporty">Sporty</MenuItem>
        </Select>
      </FormControl>

      <Button variant="contained" onClick={handleFilter}>
        Apply Filters
      </Button>
    </Box>
  );
};

export default Filters;
