// components/Filters.js
import React, { useState } from 'react';
import { Box, FormControl, InputLabel, MenuItem, Select, Button } from '@mui/material';

// Array of Indian cities
const cities = [
  "Mumbai", "Delhi", "Bangalore", "Hyderabad", "Ahmedabad", "Chennai",
  "Kolkata", "Surat", "Pune", "Jaipur", "Lucknow", "Kanpur", 
  "Nagpur", "Indore", "Thane", "Bhopal", "Visakhapatnam", 
  "Patna", "Vadodara", "Ghaziabad"
];

const Filters = () => {
  const [location, setLocation] = useState('');
  const [season, setSeason] = useState('');
  const [preference, setPreference] = useState('');

  const handleFilter = () => {
    console.log('Location:', location, 'Season:', season, 'Preference:', preference);

    // Send the selected data to the backend
    fetch('http://localhost:5001/api/filters/savePreferences', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ location, season, preference })
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Success:', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <Box sx={{ marginTop: 4 }}>
      <FormControl fullWidth sx={{ marginBottom: 2 }}>
        <InputLabel>Location</InputLabel>
        <Select value={location} onChange={(e) => setLocation(e.target.value)}>
          <MenuItem value="Mumbai">Mumbai</MenuItem>
          <MenuItem value="Delhi">Delhi</MenuItem>
          <MenuItem value="Bangalore">Bangalore</MenuItem>
          {/* Add more cities as required */}
        </Select>
      </FormControl>

      <FormControl fullWidth sx={{ marginBottom: 2 }}>
        <InputLabel>Season</InputLabel>
        <Select value={season} onChange={(e) => setSeason(e.target.value)}>
          <MenuItem value="Winter">Winter</MenuItem>
          <MenuItem value="Spring">Spring</MenuItem>
          <MenuItem value="Summer">Summer</MenuItem>
          <MenuItem value="Fall">Fall</MenuItem>
        </Select>
      </FormControl>

      <FormControl fullWidth sx={{ marginBottom: 2 }}>
        <InputLabel>Personal Preference</InputLabel>
        <Select value={preference} onChange={(e) => setPreference(e.target.value)}>
          <MenuItem value="Formal">Formal</MenuItem>
          <MenuItem value="Casual">Casual</MenuItem>
          <MenuItem value="Sporty">Sporty</MenuItem>
        </Select>
      </FormControl>

      <Button variant="contained" onClick={handleFilter}>
        Apply Filters
      </Button>
    </Box>
  );
};

export default Filters;
